import './App.css';
import { useState, useEffect } from 'react';

const Numbers = (props) => {
  return (
    <>
    <p className="equation">{props.first} {props.operator} {props.second} = </p>
    </>
  )
}


const App = () => {

  const [newEq, setNewEq] = useState(true);
  const [f, setF] = useState(null);
  const [s, setS] = useState(null);

  useEffect(() => {
    if (newEq) {
      const opNum = Math.random() * 4
      setF(Math.floor(Math.random() * 99) + 2);
      setS(Math.floor(Math.random() * 99) + 2);
      setNewEq(false);
    }

  }, [newEq]);

  return (
    <div className="App">
      <div className='rectangle'>
        <Numbers first={f} operator={"+"} second={s}></Numbers>
      </div>
    </div>
  );
}

export default App;
